import React from 'react';
import { useState,useEffect } from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import styled from "styled-components";
import { mobile } from "../responsive";

import Paper from '@mui/material/Paper';

const Item = styled(Paper)(({ theme }) => ({
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}));
const Container = styled.div`


  height: 70vh;
  position: relative;
  align-items: center;
  justify-content: center;
  margin-left: 30%;
`;





const Title = styled.h2`
    color:black;

    margin-right: 50%;
`;
const Title1 = styled.h3`
    color:black;
    margin-bottom: 100px;
    margin-right: 10%;
`;



  function App() {

  const [count, setCount] = useState();
  const [factorial1, setfactorial] = useState();

  useEffect(() => {
    const timeout = setTimeout(() => {
      setCount(count + 1);
      setfactorial(factorial(count+1))

    }, 3000);

    return () => {
      clearTimeout(timeout);
    };
  }, [count]);



  const handelinput= (e)=>{
    const numValue = parseInt(document.querySelector('input').value);
    setfactorial(factorial(numValue));
    setCount(numValue);

  }


  function factorial(n){
    let answer = 1;
    if (n === 0 || n === 1){
      return answer;
    }else{
      for(let i = n; i >= 1; i--){
        answer = answer * i;
      }
      return answer;
    }  
  }


  return (

    <Container  >
<Stack direction="row" spacing={2}>
  <Item> <Title1> Insert A number :</Title1>  </Item>
  <Item><TextField id="c" label="Outlined" variant="outlined" /></Item>
  <Item>Item 3</Item>
</Stack>


  <Button variant="contained" placeholder='count' onClick={handelinput}>Calculate</Button>

  <Title> The Factorial for {count} ={factorial1} </Title>   

    </Container>


  );
}




  export default App;
