import {
  Facebook,
  Instagram,

} from "@material-ui/icons";
import styled from "styled-components";
import { mobile } from "../responsive";
import { Send } from "@material-ui/icons";

const Container = styled.div`
background-color: #fcf5f5;

  display: flex;
  ${mobile({ flexDirection: "column" })}
`;

const Left = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  padding: 20px;
`;

const Logo = styled.h1``;

const Desc = styled.p`
  margin: 20px 0px;
`;

const SocialContainer = styled.div`
  display: flex;
`;

const SocialIcon = styled.div`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  color: white;
  background-color: #${(props) => props.color};
  display: flex;
  align-items: center;
  justify-content: center;
  margin-right: 20px;
`;


const Contabtlogo = styled.div`
  width: 40px;
  height: 40px;
  border-radius: 50%;

  display: flex;
  align-items: center;
  justify-content: center;
  margin-right: 20px;
`;
const Center = styled.div`
  flex: 1;
  padding: 20px;
  ${mobile({ display: "none" })}
`;

const Title = styled.h3`
  margin-bottom: 30px;
`;

const List = styled.ul`
  margin: 0;
  padding: 0;
  list-style: none;
  display: flex;
  flex-direction: column;
`;

const ListItem = styled.li`
  width: 50%;
  margin-bottom: 10px;
`;

const Right = styled.div`
  flex:3;
  padding: 20px;
  ${mobile({ backgroundColor: "#fff8f8" })}

`;

const ContactItem = styled.div`
  margin-bottom: 20px;
  display: flex;
  align-items: center;
`;

const Payment = styled.img`
    width: 50%;
`;
const Container1 = styled.div`

  background-color: #fcf5f5;
  display: flex;
  align-items: center;

  flex-direction: column;
`;
const Title1 = styled.h1`
  font-size: 70px;
  margin-bottom: 20px;
`;

const Desc1 = styled.div`
  font-size: 24px;
  font-weight: 300;
  margin-bottom: 20px;
  ${mobile({ textAlign: "center" })}

`;

const InputContainer1 = styled.div`
  width: 50%;
  height: 40px;
  background-color: white;
  display: flex;
  justify-content: space-between;
  border: 1px solid lightgray;
  ${mobile({ width: "80%" })}
`;

const Input1 = styled.input`
  border: none;
  flex: 8;
  padding-left: 20px;
`;

const Button1 = styled.button`
  flex: 1;
  border: none;
  background-color: teal;
  color: white;
`;
const Footer = () => {
  return (
    <Container>

      <Left>

        <Title>Fellow US AT</Title>
        <List>
          <ListItem>       <SocialContainer>
          <SocialIcon color="3B5999">
            <Facebook />
          </SocialIcon>
          <Contabtlogo >
          FaceBook
          </Contabtlogo>

          </SocialContainer> </ListItem>
          <ListItem>  <SocialContainer>
          <SocialIcon color="3B5999">

          </SocialIcon>
          <Contabtlogo >
          Youtube
          </Contabtlogo>  </SocialContainer>
          </ListItem>
          <ListItem>  
            <SocialContainer>
            <SocialIcon color="E4405F">
            <Instagram />
          </SocialIcon>
          <Contabtlogo >
          Instagram
          </Contabtlogo>  </SocialContainer></ListItem>

        </List>
        </Left>

      <Right>

        <Container1>
      <Title1>Newsletter</Title1>
      <Desc1>Get timely updates from your favorite products.</Desc1>
      <InputContainer1>
        <Input1 placeholder="Your email" />
        <Button1>
          <Send />
        </Button1>
      </InputContainer1>
    </Container1>
      </Right>
    </Container>
  );
};

export default Footer;
