import React from 'react';
import { useState,useEffect } from 'react';



    function App() {

  const [count, setCount] = useState();
  const [factorial1, setfactorial] = useState();

  useEffect(() => {
    const timeout = setTimeout(() => {
      setCount(count + 1);
      setfactorial(factorial(count+1))

    }, 3000);

    return () => {
      clearTimeout(timeout);
    };
  }, [count]);



  const handelinput= (e)=>{
    const  numValue= Number(document.querySelector('input').value);

    setfactorial(factorial(numValue))
    setCount(numValue);


  }


  function factorial(n){
    let answer = 1;
    if (n == 0 || n == 1){
      return answer;
    }else{
      for(let i = n; i >= 1; i--){
        answer = answer * i;
      }
      return answer;
    }  
  }


  return (

    <div style={{flexDirection: 'column' , display: 'flex'}}>
      Insert Number <input value=" "/>

      <button placeholder='count' onClick={handelinput}>Calculate</button>
     <p> The Factorial for {count} ={factorial1} </p>   


    </div>

  );
}




  export default App;
