import React from 'react';
import { useState } from 'react';



    function App() {
  const [count, setCount] = useState('');

  const handelinput= ()=>{

    console.log(count);
  }

  function factorial(n){
    let answer = 1;
    if (n == 0 || n == 1){
      return answer;
    }else{
      for(let i = n; i >= 1; i--){
        answer = answer * i;
      }
      return answer;
    }  
  }

  return (

    <div style={{flexDirection: 'column' , display: 'flex'}}>
      Insert Number <input placeholder='count' onChange={e=> setCount(factorial(e.target.value))} />



    <button onClick={() => handelinput()}>
      Click me!

    </button>
    {     count ? <p> Result = {count}</p>      :      ''    } 
    <p>Result={count}</p>

    </div>

  );
}




  export default App;
