import React from 'react';
import { useState,useEffect } from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import styled from "styled-components";
import { mobile } from "../responsive";

const Container = styled.div`


  height: 70vh;
  position: relative;
  align-items: center;
  justify-content: center;
  margin-left: 30%;
`;





const Title = styled.h3`
    color:black;
    margin-bottom: 20px;
    margin-right: 10%;
`;
const Title1 = styled.h3`
    color:black;
    top :100 px;
    margin-right: 10%;
`;


const PARA= styled.p`
    border:none;
    padding: 10px;
    background-color: white;
    color:gray;
    cursor: pointer;
    font-weight: 600;
`;
  function App() {

  const [count, setCount] = useState();
  const [factorial1, setfactorial] = useState();

  useEffect(() => {
    const timeout = setTimeout(() => {
      setCount(count + 1);
      setfactorial(factorial(count+1))

    }, 3000);

    return () => {
      clearTimeout(timeout);
    };
  }, [count]);



  const handelinput= (e)=>{
    const numValue = parseInt(document.querySelector('input').value);
    setfactorial(factorial(numValue));
    setCount(numValue);

  }


  function factorial(n){
    let answer = 1;
    if (n === 0 || n === 1){
      return answer;
    }else{
      for(let i = n; i >= 1; i--){
        answer = answer * i;
      }
      return answer;
    }  
  }


  return (

    <Container  >


 <Stack spacing={0} direction="row">
 <Title1> Insert A number :</Title1>  
 <TextField id="c" label="Outlined" variant="outlined" />
 </Stack>




    <Button variant="contained" placeholder='count' onClick={handelinput}>Calculate</Button>

     <Title> The Factorial for {count} ={factorial1} </Title>   

    </Container>


  );
}




  export default App;
