import React from 'react';
import { useState,useEffect } from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';

  function App() {

  const [count, setCount] = useState();
  const [factorial1, setfactorial] = useState();

  useEffect(() => {
    const timeout = setTimeout(() => {
      setCount(count + 1);
      setfactorial(factorial(count+1))

    }, 3000);

    return () => {
      clearTimeout(timeout);
    };
  }, [count]);



  const handelinput= (e)=>{
    const numValue = parseInt(document.querySelector('input').value);
    setfactorial(factorial(numValue));
    setCount(numValue);

  }


  function factorial(n){
    let answer = 1;
    if (n === 0 || n === 1){
      return answer;
    }else{
      for(let i = n; i >= 1; i--){
        answer = answer * i;
      }
      return answer;
    }  
  }


  return (

    <div >


 <Stack spacing={2} direction="row">
 <p> Insert A number  </p>  

 <Box
    component="form"
    sx={{
      '& > :not(style)': { m: 1, width: '25ch' },
    }}
    noValidate
    autoComplete="off"
  > 
    <TextField id="c" label="Outlined" variant="outlined" />
 </Box>
 <Button variant="contained" placeholder='count' onClick={handelinput}>Calculate</Button>

    </Stack>
     <p> The Factorial for {count} ={factorial1} </p>   

    </div>


  );
}




  export default App;
