import * as React from 'react';

import styled from "styled-components";
import { mobile } from "../responsive";
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { CardActionArea } from '@mui/material';
const Container = styled.div`
  flex: 1;

  height: 70vh;
  position: relative;
  align-items: center;
  justify-content: center;
  margin-left: 30%;
`;

const Image = styled.img`
  width: 50%;
  height: 50%;
  object-fit: cover;
  ${mobile({ height: "20vh" })}

`;

const Info = styled.div`
  position: absolute;
  top: 43%;
  left: 0;
  width:50%;
  height: 50%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const Title = styled.h1`
    color:black;
    margin-bottom: 20px;
`;

const Button = styled.button`
    border:none;
    padding: 10px;
    background-color: red;
    color:gray;
    cursor: pointer;
    font-weight: 600;
`;
const PARA= styled.p`
    border:none;
    padding: 10px;
    background-color: white;
    color:gray;
    cursor: pointer;
    font-weight: 600;
`;

const CategoryItem = ({ item }) => {
  return (
    <>
    <Container>

      <Image src={"https://www.enviromon.net/wp-content/uploads/downtime-calculator-cover-blue.jpg"} />
      <Info>
        <Title>Calculate Just Any Thing</Title>
        <PARA>The best website in calculations all you need in one place need not to worry from now on , we provide the service you need ,work smart , better to start now </PARA>
        <Button>Start Now </Button>
      </Info>

    </Container>

    </>
  );
};

export default CategoryItem;
