import React from 'react';
import { useState,useEffect } from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import styled from "styled-components";
import { mobile } from "../responsive";

const Container = styled.div`


  height: 70vh;
  position: relative;
  align-items: center;
  justify-content: center;
  margin-left: 30%;
`;

const Image = styled.img`
  width: 100%;
  height: 100%;
  ${mobile({ height: "20vh" })}

`;

const Info = styled.div`
  position: absolute;
  top: 43%;
  left: 0;
  width:50%;
  height: 50%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const Title = styled.h1`
    color:black;
    margin-bottom: 20px;
`;


const PARA= styled.p`
    border:none;
    padding: 10px;
    background-color: white;
    color:gray;
    cursor: pointer;
    font-weight: 600;
`;
  function App() {

  const [count, setCount] = useState();
  const [factorial1, setfactorial] = useState();

  useEffect(() => {
    const timeout = setTimeout(() => {
      setCount(count + 1);
      setfactorial(factorial(count+1))

    }, 3000);

    return () => {
      clearTimeout(timeout);
    };
  }, [count]);



  const handelinput= (e)=>{
    const numValue = parseInt(document.querySelector('input').value);
    setfactorial(factorial(numValue));
    setCount(numValue);

  }


  function factorial(n){
    let answer = 1;
    if (n === 0 || n === 1){
      return answer;
    }else{
      for(let i = n; i >= 1; i--){
        answer = answer * i;
      }
      return answer;
    }  
  }


  return (

    <div >


 <Stack spacing={2} direction="row">
 <h3> Insert A number  </h3>  

 <Box
    component="form"
    sx={{
      '& > :not(style)': { m: 1, width: '25ch' },
    }}
    noValidate
    autoComplete="off"
  > 
    <TextField id="c" label="Outlined" variant="outlined" />
 </Box>

    </Stack>
    <Button variant="contained" placeholder='count' onClick={handelinput}>Calculate</Button>

     <h3> The Factorial for {count} ={factorial1} </h3>   

    </div>


  );
}




  export default App;
